code.rust:
use mathhook_core::{symbol, expr, simplify};

let x = symbol!(x);
let f = expr!(3*(x^2) + 5*x - 2*(x^2) + 7*x + 4);

let simplified = simplify(&f);
println!("Original: {}", f);
println!("Simplified: {}", simplified);

code.nodejs:
const { symbol, expr, simplify } = require('mathhook-node');

const x = symbol('x');
const f = expr(expr(3).mul(x.pow(2)).add(expr(5).mul(x))
              .sub(expr(2).mul(x.pow(2))).add(expr(7).mul(x)).add(4));

const simplified = simplify(f);
console.log(`Original: ${f}`);
console.log(`Simplified: ${simplified}`);

code.rust:
