code.nodejs:
const { symbol, expr, factor } = require('mathhook-node');

const x = symbol('x');
const f = expr(x.pow(2).sub(expr(5).mul(x)).add(6));

const factored = factor(f);
console.log(`Original: ${f}`);
console.log(`Factored: ${factored}`);

code.rust:
use mathhook_core::{symbol, expr, simplify, sqrt};

let f = expr!(1 / (sqrt(&expr!(2)) + expr!(1)));

let rationalized = simplify(&f, SimplifyOptions::rationalize());
println!("Original: {}", f);
println!("Rationalized: {}", rationalized);

code.nodejs:
const { symbol, expr, simplify, sqrt } = require('mathhook-node');

const f = expr(expr(1).div(sqrt(2).add(1)));

const rationalized = simplify(f, { rationalize: true });
console.log(`Original: ${f}`);
console.log(`Rationalized: ${rationalized}`);
