code.rust:
use mathhook_core::{symbol, expr, diff};

let x = symbol!(x);
let f = expr!(x ^ 5);

let derivative = diff(&f, &x);
println!("f(x) = {}", f);
println!("f'(x) = {}", derivative);

// Evaluate at a point
let result = derivative.substitute(&x, &expr!(2));
println!("f'(2) = {}", result);

code.nodejs:
const { symbol, expr, diff } = require('mathhook-node');

const x = symbol('x');
const f = expr(x.pow(5));

const derivative = diff(f, x);
console.log(`f(x) = ${f}`);
console.log(`f'(x) = ${derivative}`);

// Evaluate at a point
const result = derivative.subs(x, 2);
console.log(`f'(2) = ${result}`);

