use mathhook_core::{symbol, expr, diff};
use mathhook_core::functions::elementary::trig::sin;

let x = symbol!(x);
let f = expr!((x^2) * sin(&expr!(x)));

let derivative = diff(&f, &x);
println!("d/dx[x²sin(x)] = {}", derivative);

