code.rust:
use mathhook_core::{symbol, expr, integrate};

let x = symbol!(x);
let f = expr!(x ^ 3);

let integral = integrate(&f, &x);
println!("∫ x³ dx = {}", integral);

code.nodejs:
const { symbol, expr, integrate } = require('mathhook-node');

const x = symbol('x');
const f = expr(x.pow(3));

const integral = integrate(f, x);
console.log(`∫ x³ dx = ${integral}`);

code.rust:
use mathhook_core::{symbol, expr, integrate_definite};

let x = symbol!(x);
let f = expr!(x ^ 2);

let result = integrate_definite(&f, &x, &expr!(0), &expr!(2));
println!("∫₀² x² dx = {}", result);

code.nodejs:
const { symbol, expr, integrate } = require('mathhook-node');

const x = symbol('x');
const f = expr(x.pow(2));

const result = integrate(f, [x, 0, 2]);
console.log(`∫₀² x² dx = ${result}`);

code.rust:
use mathhook_core::{symbol, expr, integrate};
use mathhook_core::functions::elementary::trig::cos;

let x = symbol!(x);
let f = expr!(2*x * cos(&expr!(x^2)));

let integral = integrate(&f, &x);
println!("∫ 2x·cos(x²) dx = {}", integral);

code.nodejs:
const { symbol, expr, integrate, cos } = require('mathhook-node');

const x = symbol('x');
const f = expr(expr(2).mul(x).mul(cos(x.pow(2))));

const integral = integrate(f, x);
console.log(`∫ 2x·cos(x²) dx = ${integral}`);
