code.nodejs:
const { symbol, expr, limit } = require('mathhook');

const x = symbol('x');

// One-sided limits
const f = expr('1/x');
const limLeft = limit(f, x, 0, { dir: 'left' });
const limRight = limit(f, x, 0, { dir: 'right' });

// At infinity
const g = expr('(3*x^2 + 2)/(x^2 - 1)');
const limInf = limit(g, x, Infinity);
console.log(`lim[x→∞] = ${limInf}`);  // 3
