code.rust:
let x = symbol!(x);
let f = expr!(x ^ 3);
let df = f.derivative(&x, 1);
// Result: 3*x^2

code.nodejs:
const x = symbol('x');
const f = expr('x^3');
const df = f.derivative(x);
// Result: 3*x^2

code.rust:
let x = symbol!(x);
let f = expr!(sin(x ^ 2));
let df = f.derivative(&x, 1);
// Result: 2*x*cos(x^2)

code.nodejs:
const x = symbol('x');
const f = expr('sin(x^2)');
const df = f.derivative(x);
// Result: 2*x*cos(x^2)

code.rust:
let x = symbol!(x);
let f = expr!((x ^ 2) * sin(x));
let df = f.derivative(&x, 1);
// Result: 2*x*sin(x) + x^2*cos(x)

code.nodejs:
const x = symbol('x');
const f = expr('x^2 * sin(x)');
const df = f.derivative(x);
// Result: 2*x*sin(x) + x^2*cos(x)
