code.rust:
use mathhook::prelude::*;

fn main() {
    let x = symbol!(x);
    let expr = expr!(x ^ 2);
    println!("Expression: {}", expr);
}

code.nodejs:
import { Expression } from 'mathhook-node';

const x = Expression.symbol('x');
const expr = x.pow(2);
