// General solution: y = 1/(1 + C*e^(-x))
let solution = solver.solve(&rhs, &y, &x, None)?;

// With y(0) = 0.1 (10% initial population)
let ic = (expr!(0), expr!(0.1));
let particular = solver.solve(&rhs, &y, &x, Some(ic))?;
println!("{}", particular); // y = 1/(1 + 9*e^(-x))

