code.rust:
// Trigamma (n=1): ψ^(1)(1) = π²/6
let result = polygamma(1, &expr!(1));
// Result: pi^2/6

// Tetragamma (n=2)
let result = polygamma(2, &expr!(1));
// Result: -2*zeta(3)

// Higher orders
let result = polygamma(3, &expr!(1.5));
// Result: numerical value

