code.rust:
// Integer values: B(2, 3) = 1/12
let result = beta(&expr!(2), &expr!(3));

// Numerical evaluation
let result = beta(&Expression::float(2.5), &Expression::float(3.7));

// Symmetry property
assert_eq!(beta(&expr!(2), &expr!(5)), beta(&expr!(5), &expr!(2)));

