code.rust:
use mathhook::prelude::*;

let x = symbol!(x);

// Identity elements
let expr = expr!((x + 0) * 1);
let simplified = expr.simplify();
// Result: x

// Constant folding
let expr = expr!(2 + 3);
let simplified = expr.simplify();
// Result: 5

code.nodejs:
const { symbol } = require('mathhook');

const x = symbol('x');

// Identity elements
let expr = x.add(0).mul(1);
const simplified = expr.simplify();
// Result: x

code.rust:
use mathhook::prelude::*;

let x = symbol!(x);

// Combine like powers
let expr = expr!((x^2) * (x^3));
let simplified = expr.simplify();
// Result: x^5

// Multiple powers
let expr = expr!((x^2) * (x^3) * (x^4));
let simplified = expr.simplify();
// Result: x^9

code.nodejs:
const { symbol } = require('mathhook');

const x = symbol('x');

// Combine like powers
const expr = x.pow(2).mul(x.pow(3));
const simplified = expr.simplify();
// Result: x^5

code.rust:
use mathhook::prelude::*;

let A = symbol!(A; matrix);
let B = symbol!(B; matrix);

// Matrix multiplication does NOT commute
let expr = expr!(A * B);
// Simplification preserves order: A*B ≠ B*A

code.nodejs:
const { symbol } = require('mathhook');

const A = symbol('A', { type: 'matrix' });
const B = symbol('B', { type: 'matrix' });

// Matrix multiplication does NOT commute
const expr = A.mul(B);
// Simplification preserves order: A*B ≠ B*A
